import styled from "styled-components";

export const Container = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 5rem 15rem;
  background-color: #2b2b2b;
  color: white;

  .hero-text {
    flex: 1;
    display: flex;
    flex-direction: column;
    align-items: flex-start;
    gap: 1rem;

    h1 {
      font-size: 3.5rem;
      color: #fff;
    }

    h3 {
      font-size: 2rem;
      color: #fff;
    }

    .button {
      margin-top: 2rem;
      padding: 0.8rem 2rem;
      background-color: #4CAF50;
      color: white;
      text-transform: uppercase;
      border: none;
      border-radius: 1rem;
      font-weight: bold;
      cursor: pointer;
      transition: background-color 0.3s;

      &:hover {
        background-color: #45a049;
      }
    }

    .social-media {
      display: flex;
      align-items: center;
      gap: 1rem;
      margin-top: 1.5rem;

      a {
        display: inline-flex;
        align-items: center;
        justify-content: center;
        width: 2.5rem;
        height: 2.5rem;
        border-radius: 50%;
        background-color: #333;
        transition: background-color 0.3s;

        img {
          width: 1.5rem;
          height: 1.5rem;
        }

        &:hover {
          background-color: #555;
        }
      }
    }
  }

  .hero-image {
    flex: 1;

    img {
      max-width: 100%;
      height: auto;
    }
  }

  @media (max-width: 800px) {
    padding: 3rem;
    flex-direction: column;

    .hero-text {
      align-items: center;
      text-align: center;

      h1 {
        font-size: 3rem;
      }

      h3 {
        font-size: 1.8rem;
      }

      .social-media {
        justify-content: center;
      }
    }

    .hero-image {
      margin-top: 2rem;
    }
  }

  @media (max-width: 600px) {
    padding: 2rem;

    .hero-text {
      h1 {
        font-size: 2.5rem;
      }

      h3 {
        font-size: 1.5rem;
      }

      .button {
        padding: 0.6rem 1.5rem;
      }
    }
  }
`;
